import { data } from "../Data/Data"
import {useState } from "react";

export default function Main(){
    const [tpc,setTpc]=useState();
    const [selectedDate,setselectedDate]=useState();
    const [firstRev,setFirstRev]=useState();
    const [secondRev,setSecondRev]=useState();
    
     
    function handleTpc(e){
        setTpc(e.target.value);
    }

    function handleDateChange(e){
        setselectedDate(e.target.value);
    }
    return(<>
    <h1>This is main page</h1>
    <label>Enter Topic:</label><br></br>
    <input value={tpc} onChange={handleTpc}></input>
    <br></br>
    <label>Enter Date:</label>
    <input 
        type="date" 
        value={selectedDate}
        onChange={handleDateChange} 
      />
      <label>First Revision</label><br></br>
      <input value={firstRev} onChange={(e)=>{setFirstRev(e.target.value)}}></input>
      <label>Second Revision</label><br></br>
      <input value={secondRev} onChange={(e)=>{setSecondRev(e.target.value)}}></input>
    </>)
}
