import { data } from "../Data/Data"
import {useState ,useEffect} from "react";
import './Main.css';

export default function Main(){
    const [tpc,setTpc]=useState();
    const [selectedDateTo,setselectedDateTo]=useState("");
    const [selectedDateFrom,setselectedDateFrom]=useState("");
    const [frequencyList,setFL]=useState([])
    const [firstRev,setFirstRev]=useState();
    const [secondRev,setSecondRev]=useState();
   // let frequencyList=[];
    
     useEffect(()=>{
        data.map((d)=>{
            let fd = new Date(d.startDate);  // Create a copy of the input date to avoid modifying the original
  fd.setDate(fd.getDate() + 7*d.firstRevision); 
  let sd = new Date(d.startDate);  // Create a copy of the input date to avoid modifying the original
  sd.setDate(sd.getDate() + 7*d.secondRevision);
  // Add 14 days to the date
//   return result;
//  let fd=new Date(d.startDate).getDate()+7*d.firstRevision;
//  let sd=new Date(d.startDate).getDate()+7*d.secondRevision;
  d.fd=fd;
  d.sd=sd;
        });
       // console.log(data);

     },[]);
    function handleTpc(e){
        setTpc(e.target.value);
    }
   
    function showTpc(){
        if(selectedDateTo!=="" && selectedDateFrom!==""){
       let a=[];
       let b=[];
             a=data.filter((d)=>{
                let fd = new Date(d.fd); 
                fd.setDate(fd.getDate() + 7); 
                return new Date(d.fd)>=new Date (selectedDateTo) && new Date (selectedDateFrom)>=new Date(d.fd);
               
            });



            
            
             b=data.filter((d)=>{
                let sd = new Date(d.sd); 
                sd.setDate(sd.getDate() + 7); 
                return new Date(d.sd)>=new Date (selectedDateTo) && new Date (selectedDateFrom)>=new Date(d.sd);
               
            });

            let c=new Set([...a,...b]);
            let d=[...c];
            const today = new Date();
console.log(today);
console.log(d);
            d.forEach((e)=>{
                e.due={
                    fd: new Date(e.fd).toDateString()===today.toDateString()?'highlight':"",
                    sd: new Date(e.sd).toDateString()===today.toDateString()?'highlight':"",

                };
                
            })
            
            setFL([...d]);
           
                }
    }


    return(<div className="container">
    <h1>This is main page</h1>

      <input 
        type="date" 
        value={selectedDateTo}
        onChange={(e)=>{
            setselectedDateTo(e.target.value);
            setselectedDateFrom("");
           }} 
      />
      
      <input 
        type="date" 
        value={selectedDateFrom}
        min={selectedDateTo}
        onChange={(e)=>{
            setselectedDateFrom(e.target.value);
        }} 
      />

      <button onClick={showTpc} className="buttonContainer">Show Topics</button>
      <table>
        <thead>
        <tr>
                <th>Topic</th>
                <th>Last Revision</th>
                <th>1st Revision</th>
                <th>2nd Revision</th>
            </tr>
        </thead>
        <tbody>
        {frequencyList.length>0 && frequencyList.map((f)=>
    
    <tr>
        <td>{f.tpc}</td>
        <td>{new Date(f.startDate).toLocaleDateString()}</td>
        <td className={f.due.fd}>{f.fd.toLocaleDateString()}</td>
        <td className={f.due.sd}>{f.sd.toLocaleDateString()}</td>
    </tr>
    

    )}
        </tbody>
      </table>
     

    {!frequencyList.length>0 && <div>No Topics found</div>}

    
    </div>)
}
