import { data } from "../Data/Data"
import {useState ,useEffect} from "react";
//import './Main.css';

import SubmitPage from "./SubmitPage";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Main(){
    let navigate=useNavigate();
    const todayDate=new Date().toISOString().split("T")[0]
    const [selectedDateTo,setselectedDateTo]=useState(todayDate);
    const [selectedDateFrom,setselectedDateFrom]=useState(todayDate);
    const [frequencyList,setFL]=useState([]);
   // const[frequencyListUpdated,]
    const [data,setData]=useState();
    let newList=[];

    useEffect(() => {
        fetch('http://localhost:5000/api/data')
          .then(response => response.json())
          .then(data => {
          //  console
            setData(data); // Set the fetched data into the state
          })
          .catch(error => {
            console.error('Error fetching data:', error); // Handle errors
          });
      }, []);
    
      console.log(data);
    //  useEffect(()=>{
        if(data!==undefined){
        data.map((d)=>{
            let fd = new Date(d.startDate);  // Create a copy of the input date to avoid modifying the original
  fd.setDate(fd.getDate() + Number(d.firstRevision)); 
  let sd = new Date(d.startDate);  // Create a copy of the input date to avoid modifying the original
  sd.setDate(sd.getDate() + Number(d.secondRevision));
  // Add 14 days to the date
//   return result;
//  let fd=new Date(d.startDate).getDate()+7*d.firstRevision;
//  let sd=new Date(d.startDate).getDate()+7*d.secondRevision;
  d.fd=fd;
  d.sd=sd;
        });

        
    }
       // console.log(data);

    //  },[data]);

    function resetTime(date) {
        const resetDate = new Date(date);
        resetDate.setHours(0, 0, 0, 0);  // Reset time to 00:00:00
        return resetDate;
      }

    if(data!==undefined && selectedDateTo!==new Date() && selectedDateFrom!==new Date()){
        let a=data.filter((d)=>{
            //return new Date(d.fd)===new Date(selectedDateTo);
            return resetTime(new Date(d.fd)).getTime() === resetTime(new Date(selectedDateTo)).getTime()
        });
        a.forEach((e)=>{
            e.due={
                fd: new Date(e.fd).toDateString()===new Date().toDateString()?'highlight':"",
                sd: new Date(e.sd).toDateString()===new Date().toDateString()?'highlight':"",

            };
            
        })
        
        //setFL([...data]);
        newList=[...a]
    }
   
   
    function showTpc(){
        if(selectedDateTo!=="" && selectedDateFrom!==""){
       let a=[];
       let b=[];
             a=data.filter((d)=>{
                let fd = new Date(d.fd); 
                fd.setDate(fd.getDate()+7); 
                return new Date(d.fd)>=new Date (selectedDateTo) && new Date (selectedDateFrom)>=new Date(d.fd);
               
            });



            
            
             b=data.filter((d)=>{
                let sd = new Date(d.sd); 
                sd.setDate(sd.getDate() + 7); 
                return new Date(d.sd)>=new Date (selectedDateTo) && new Date (selectedDateFrom)>=new Date(d.sd);
               
            });

            let c=new Set([...a,...b]);
            let d=[...c];
            const today = new Date();
console.log(today);
console.log(d);
            d.forEach((e)=>{
                e.due={
                    fd: new Date(e.fd).toDateString()===today.toDateString()?'highlight':"",
                    sd: new Date(e.sd).toDateString()===today.toDateString()?'highlight':"",

                };
                
            })
            
            setFL([...d]);
           
                }
    }


    return(<div className="submitContainer">
    <h1>Topics List</h1>
    {/* <SubmitPage></SubmitPage> */}

    {/* <button onClick={handleSubmit}>Submit</button> */}

      <input 
        type="date" 
        value={selectedDateTo}
        onChange={(e)=>{
            setselectedDateTo(e.target.value);
            setselectedDateFrom("");
           }} 
      />
      
      <input 
        type="date" 
        value={selectedDateFrom}
        min={selectedDateTo}
        onChange={(e)=>{
            setselectedDateFrom(e.target.value);
        }} 
      />

      <button onClick={showTpc} className="buttonContainer">Show Topics</button>
      
      <table>
        <thead>
        <tr>
                <th>Topic</th>
                <th>Last Revision</th>
                <th>1st Revision</th>
                <th>2nd Revision</th>
            </tr>
        </thead>
        <tbody>
        {newList.length>0 && newList.map((f)=>
    
    <tr>
        <td>{f.tpc}</td>
        <td>{new Date(f.startDate).toLocaleDateString()}</td>
        <td className={f.due.fd}>{f.fd.toLocaleDateString()}</td>
        <td className={f.due.sd}>{f.sd.toLocaleDateString()}</td>
    </tr>
    

    )}
        </tbody>
      </table>
     

    {!newList.length>0 && <div>No Topics found</div>}

    <button onClick={()=>{navigate("/")}} className="buttonContainer">Back To Enter</button>
    </div>)

    
}
