import {useState ,useEffect} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
export default function SubmitPage(){
const [tpc,setTpc]=useState();
const [startDate,setStartDate]=useState();
const[fr,setFr]=useState();
const[sr,setSr]=useState();
let navigate=useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = {
        id:parseInt(Math.random()*100),
        tpc:tpc,
        startDate:startDate,
        firstRevision:fr,
        secondRevision:sr
    };

    try {
      // Send the data to the backend
      const response = await axios.post("http://localhost:5000/api/update", data);
      console.log("Data updated:", response.data);
    } catch (error) {
      console.error("Error updating data:", error);
    }
  
    setTpc("");
    setFr("");
    setSr("");
    setStartDate("");

  };

  function handleListPage(){
    navigate("/list");
  }

    return(<div className="container">
        <h2>This is main page</h2>
    <div>
    <label>Enter Topic</label><br></br>
    <input value={tpc} onChange={(e)=>setTpc(e.target.value)}></input>
    </div>
    <div>
    <label>Revision Date</label><br></br>
   
    <input 
        type="date" 
        value={startDate}
        onChange={(e)=>{
            setStartDate(e.target.value);
            
           }} 
      />
    </div>
    <div>
    <label>1st Revision</label><br></br>
    <input value={fr} onChange={(e)=>setFr(e.target.value)}></input>
    </div>
    <div>
    <label>2nd Revision</label><br></br>
    <input value={sr} onChange={(e)=>setSr(e.target.value)}></input>
    </div>
    <div>
    <button onClick={handleSubmit}>Submit</button>
    <button onClick={handleListPage}>Review List</button>
    </div>
    
    
    
    </div>)
}
